mapper.update:
 
	update t_member
		set member_name=#{member_name}, member_address=#{member_address}, member_email=#{member_email}, 
		member_phone=#{member_phone}
		where member_id=#{member_id}

 
	update t_member
		set member_pwd=#{member_pwd}, member_salt=#{member_salt}
		where member_id=#{member_id}
