mapper.select:
select s.shop_NO, s.member_NO, s.shop_name, s.shop_address, s.shop_code, d.shopDetail_NO, d.shop_NO, d.shop_ceo, d.shop_phone, d.shop_open_time, d.shop_close_time, d.shop_introduce, d.shop_starScore, d.shop_imageMain, d.shop_imageSub, d.shop_heartScore, d.shop_reserveScore, d.shopDetail_joinDate
from t_shopDetail d inner join t_shop s on d.shop_NO = s.shop_NO
inner join t_member m on s.member_NO = m.member_NO where m.member_NO = #{member_NO}

 
		
			select s.shop_NO, s.member_NO, s.shop_name, s.shop_address, s.shop_code
				from t_shop s
